mapper.select:
SELECT * FROM ( SELECT inner_table.* , ROWNUM AS row_seq FROM ( SELECT post_no, user_id, title, text, thumbnail_image_file, reg_date, view_count, hashtag FROM post
title LIKE '%${searchKeyword}%' AND
text LIKE '%${searchKeyword}%' AND
hashtag LIKE '%${searchKeyword}%' AND
flag_pu='UC'
) inner_table WHERE ROWNUM <= #{endRowNum} ) WHERE row_seq BETWEEN #{startRowNum} AND #{endRowNum}

mapper.update:
UPDATE post
SET title=#{title}, text=#{text}, thumbnail_image_file=#{thumbNailFileName}, reg_date=SYSDATE, lat=#{lat}, lng=#{lng}, hashtag=#{hashtag}
WHERE post_no=#{postNo}
