mapper.select:
		SELECT * FROM (SELECT ROWNUM row_num, inner_table.*
		FROM (SELECT comment_no, user_id, post_no, text, reg_date FROM comments WHERE user_id=#{searchKeyword}
 		ORDER BY reg_date DESC) inner_table
 		WHERE ROWNUM <= #{endRowNum} )
		WHERE row_num BETWEEN #{startRowNum} AND #{endRowNum}

mapper.update:
UPDATE comments
SET text=#{text}
WHERE comment_no=#{commentNo}

mapper.select:
	SELECT COUNT(*)
  	FROM(	SELECT comment_no, user_id, text, reg_date 
  	FROM comments 
  	WHERE post_no=#{postNo}
					) countTable

	SELECT COUNT(*)
  	FROM(	SELECT comment_no, user_id, text, reg_date 
  	FROM comments 
  	WHERE user_id=#{userId}
					) countTable
