mapper.update:
UPDATE groups
group_name = #{groupName}, title = #{title}, text = #{text}, lat = #{lat:VARCHAR}, lng = #{lng:VARCHAR}, category_code = #{categoryCode}, meeting_date = #{meetingDate:VARCHAR}
WHERE group_no = #{join.groupNo}

mapper.select:
SELECT * FROM ( SELECT inner_table.* , ROWNUM AS row_seq FROM ( SELECT p.prod_no, p.prod_name, p.prod_detail, p.manufacture_day, p.price, p.image_file, p.reg_date, t.tran_status_code FROM group p, transaction t WHERE p.prod_no=t.prod_no(+)
AND p.prod_no = #{searchKeyword}
AND p.prod_name LIKE '%${searchKeyword}%'
AND p.price = #{searchKeyword}
AND t.tran_status_code IS NULL
AND t.tran_status_code=1
AND t.tran_status_code=2
AND t.tran_status_code=3
ORDER BY p.price ASC
ORDER BY p.price DESC
ORDER BY p.reg_date ASC
ORDER BY p.reg_date DESC
) inner_table WHERE ROWNUM <= #{endRowNum} ) WHERE row_seq BETWEEN #{startRowNum} AND #{endRowNum}
